import React from "react";
import Intro from "../components/Intro/Intro.js";
import Section from '../components/Section/Section.js';

import { projectProps } from '../components/data/Data.js'

import AffinityDiagram from './TripPicMedia/AffinityDiagram.png';
import CustomerJourneyMap from './TripPicMedia/CustomerJourneyMap.png';
import UserFlow from './TripPicMedia/UserFlow.png'
import Crazy8s from './TripPicMedia/Crazy8s.png'
import SpeedDating from './TripPicMedia/SpeedDating.png'
import ConceptModel from './TripPicMedia/ConceptModel.png'
import Wireframes from './TripPicMedia/Wireframes.png'

function TripPic() {

    const item = projectProps('trip_pic');

    return (
        <div className="body">
            <Intro 
                key={ item.Component }
                component={ item.Component }
                title={ item.Title }
                highlight={ item.Highlight }
                tags={ item.Tags }
                dates={ item.Dates }
                summary={ item.Summary }
                link={ item.Link }
                linkTitle={ item.LinkTitle }
                responsibilities={ item.Responsibilities }
                tools={ item.Tools }
                team={ item.Team }
            />
            <Section 
                title="Problem Space"
            >
                <h3>How might we help young tech-savvy travelers quickly and easily discover travel experiences from sources that they trust and conveniently consolidate those experiences into an efficient, concrete schedule?</h3>
                <br></br>
                <p>
                    Given that there are so many different sources of information when traveling, it is often difficult to pinpoint what is reliable and desirable. We chose to further investigate how tech savvy travelers plan for unknown destinations and how we can bring the most enjoyable experience to the often tedious process of finding and organizing itineraries. 
                </p>
            </Section>
            <Section 
                title="Contextual Research and Analysis"
            >
                <h3>Think Aloud Interviews</h3>
                <br></br>
                <p>
                    We conducted think aloud interviews to observe how young tech-savvy travelers plan for a trip to an unfamiliar destination. By asking users to demonstrate how they would plan for a trip to an unfamiliar destinations they are interested in, we found what tools and behaviors people typically employ when travel planning and where we can make the process easier.                
                </p>
                <h3>Think Aloud Interviews</h3>
                <br></br>
                <p>
                    We conducted think aloud interviews to observe how young tech-savvy travelers plan for a trip to an unfamiliar destination. By asking users to demonstrate how they would plan for a trip to an unfamiliar destinations they are interested in, we found what tools and behaviors people typically employ when travel planning and where we can make the process easier.                
                </p>
            </Section>
            <Section 
                title="Quotes/Evidence"
            >
                <h3>How might we help young tech-savvy travelers quickly and easily discover travel experiences from sources that they trust and conveniently consolidate those experiences into an efficient, concrete schedule?</h3>
                <br></br>
                <p>
                    Given that there are so many different sources of information when traveling, it is often difficult to pinpoint what is reliable and desirable. We chose to further investigate how tech savvy travelers plan for unknown destinations and how we can bring the most enjoyable experience to the often tedious process of finding and organizing itineraries. 
                </p>
            </Section>

        </div>
    )
}

export default TripPic;
