import React from "react";
import ReactDOM from 'react-dom';
// import '../App.css';
import './ProjectSection.css';

function ProjectSections(props) {
    // const imgFolder = require.context('./project-media/', useSubdirectories = false)
    // const listOfImages = images(`./${props.title}.png`);
    // const listOfImages = images(`./${props.title}.png`);

    // var listOfImages = importAll(require.context("./project-media/24", false, /\.(png|jpe?g)$/));;
    // var listOfVideos = importAll(require.context('./project-media/30 Day Adulting', false, /\.(mov)$/));;

    // const directory = require.context("./project-media", false, /\.(png|jpe?g|svg)$/);
    // let imagePaths = getImagePaths(directory);

    // let images = [];
    // let folder = props.title;
    // imagePaths.map(({ folder }) => images.push(require("../" + { folder })));

    // const images = importAll(require.context('./project-media', true, /\.(png|jpe?g|svg)$/));

    const importAll = require =>
        require.keys().reduce((acc, next) => {
            acc[next.replace("./", "")] = require(next);
            return acc;
        }, {});

    const images = importAll(
        require.context("./project-media", false, /\.(png|jpe?g|svg)$/)
    );

    return (
        <div className='project-card'>
            <div className="one-line">
                <h3 className="one-line-item">{ props.title }</h3>
                <h5 className="one-line-item project-dates">{ props.dates }</h5>
            </div>
            <p>{ props.body }</p>
            {
                props.link === '' 
                ? <></>
                : <p>For more information, click <a className="link-color" href={ props.link } target="_blank">here</a></p> 
            }
            <div id="gallery-container">
                {images.length>0?
                <>
                {
                    images.map((img, index) => (
                        <div>
                        <img key={index} src={img} alt={img} />
                        </div>
                    ))}
                </> : <h1>{projectArr.length}</h1>}
            </div>

        </div>
    );
}

function importAll(r) {
    return r.keys().map(r);
}

export default ProjectSections;
