import React from 'react'
import { NavLink } from 'react-router-dom'
import './Footer.css'

import Socials from '../Socials/Socials.js'
import UXPage from '../UXProjects/UXProjects.js'

function Footer() {

    const scrollToTop = () => {
		window.scrollTo({top: 0, behavior: 'smooth'});
	};

    const scrollTo = (ele) => {
        ele.scrollIntoView({
          behavior: "smooth",
          block: "start",
        });
    };

    return (
        <div className='footer-background'>
            <div className='footer-container body'>
                <div className='footer-left'>
                    <p>Interested in seeing more?</p>
                    <br></br>
                    <p>
                        Check out my 
                        <NavLink to='/' onClick={() => {
                                            scrollTo(operationsRef.current);
                                        }}>
                            UX design projects.
                        </NavLink>
                    </p>
                    <p>
                        Check out my front-end/full stack development projects.
                    </p>
                </div>
                <div className='footer-right'>
                    <p>Made with React, 2021</p>
                    <Socials/>
                </div>
            </div>
        </div>
    )
}

export default Footer
