import React from "react";
import Intro from "../components/Intro/Intro.js";
import Section from '../components/Section/Section.js';
import Sub from '../components/SubSection/SubSection.js';

import { projectProps } from '../components/data/Data.js';

function TripPic() {

    const item = projectProps('trip_pic');

    return (
        <div className="body">
            <Intro 
                key={ item.Component }
                component={ item.Component }
                title={ item.Title }
                highlight={ item.Highlight }
                tags={ item.Tags }
                dates={ item.Dates }
                summary={ item.Summary }
                link={ item.Link }
                linkTitle={ item.LinkTitle }
                responsibilities={ item.Responsibilities }
                tools={ item.Tools }
                team={ item.Team }
            />
            <Section 
                title="Problem Space"
            >
                <Sub 
                    header="How might we help young tech-savvy travelers quickly and easily discover travel experiences from sources that they trust and conveniently consolidate those experiences into an efficient, concrete schedule?"
                    body=""
                />
            </Section>
            <Section 
                title="Contextual Research and Analysis"
            >
                <Sub 
                    header="Think Aloud Interviews"
                    body="We conducted think aloud interviews to observe how young tech-savvy travelers plan for a trip to an unfamiliar destination. By asking users to demonstrate how they would plan for a trip to an unfamiliar destinations they are interested in, we found what tools and behaviors people typically employ when travel planning and where we can make the process easier."
                />
                <Sub 
                    header="Quotes and Evidence"
                    body=" We conducted think aloud interviews to observe how young tech-savvy travelers plan for a trip to an unfamiliar destination. By asking users to demonstrate how they would plan for a trip to an unfamiliar destinations they are interested in, we found what tools and behaviors people typically employ when travel planning and where we can make the process easier."
                />
            </Section>
            <Section 
                title="Quotes/Evidence"
            >
                <h3>How might we help young tech-savvy travelers quickly and easily discover travel experiences from sources that they trust and conveniently consolidate those experiences into an efficient, concrete schedule?</h3>
                <br></br>
                <p>
                    Given that there are so many different sources of information when traveling, it is often difficult to pinpoint what is reliable and desirable. We chose to further investigate how tech savvy travelers plan for unknown destinations and how we can bring the most enjoyable experience to the often tedious process of finding and organizing itineraries. 
                </p>
            </Section>

        </div>
    )
}

export default TripPic;
