import React from "react";
import './FeaturedProjectSection.css'; 
import '../../App.css'

import ThirtyDayDemo from '../ProjectSection/project-media/30 Day Adulting/30-day-demo.mov';
import SentenceMosaics from '../ProjectSection/ProjectMediaComponents/SentenceMosaics.js';
import cccImg from  '../ProjectSection/project-media/Cross Country Connections/ccc-mother.jpeg'


function FeaturedProjectSections(props) {

    const projectSum = props.summary.toString().trim();
    return (
        <>
            <div className='featured-project-card'>
                <div className="featured-text">
                    <h3 className="featured-header">{ props.title }</h3>
                    <p>{ projectSum }</p>
                </div>
            </div>
        </>
    );

}

// function to formas a string to include newlines, bullet points, and website
// links. Used for all text on the project page.
function formatText(str) {
    if (!str) return str;

    // replace \n in the string with <br />
    let formatted = [];
    const lines = str.split("\\n");

    for (let line of lines) {
        // make a bullet whenever there's a \b in the string
        let bullets = line.split("\\b");

        formatted = findLink(formatted, bullets[0]); // first entry isn't a bullet

        bullets.shift(); // removes the non-bullet
        if (bullets.length === 0) {
            // if there was only text, no bullets, include a break
            formatted.push(<br />);
        } else {
            // otherwise, add an unordered list
            let lis = bullets.map(bullet => <li>{ findLink([], bullet) }</li>);
            formatted.push(<ul>{ lis }</ul>);
        }
    }

    return formatted; 
}

export default FeaturedProjectSections;
