                <h3 className="one-line-item">{ props.title }</h3>
                <h5 className="one-line-item project-dates">{ props.dates }</h5>
            </div>
            <p>{ props.body }</p>
            {
                props.link === '' 
                ? <></>
                : <p>For more information, click <a className="link-color" href={ props.link } target="_blank">here</a></p> 
            }
            <div id="gallery-container">
                {images.map((img, index) => (
                    <div>
                    <img key={index} src={img} alt={img} />
                    </div>
                ))}
            </div>

        </div>
    );
}

function importAll(r) {
    return r.keys().map(r);
}

function getImagePaths(directory) {
    let images = [];
    directory.keys().map((item, index) => images.push(item.replace("./", "")));
    return images;
}

export default ProjectSections;
