import './Pages.css';
import Card from '../ProjectCard/ProjectCard.js';
import Footer from '../Footer/Footer.js'
import Papa from "papaparse";

function Projects() {

  const strRead = `Component,Title,Dates,Summary,Link,LinkTitle,Tags,Responsibilities,Tools,Team~
  thirty_day_adulting,30-Day Adulting,May 2021 - present,Developed an adulting challenge iOS application that uses embedded design and social influence principles to effectively teach and encourage cybersecurity habits.\nWorking as a research assistant for the CoEx lab at CMU.,,,"Research,Figma,Xcode • Swift,Firebase,Mobile App","App Development, UI/UX, Research",Xcode\nFirebase\nFigma,"Isadora Krsek, Nellie Tonev, Sherry Chen"~
