import React from "react";
import "./Section.css"
import {formatText} from '../data/Data.js'

/*  Section is a component that consistently styles sections with two columns, 
    used on the detail view of each Project
*/

function Section(props) {
    return (
        <div className="section-container" id={ props.id }>
            {
                props.title === undefined 
                ?   <></>
                :   <div>
                        <h4>{ props.title }</h4>
                        <br></br>
                    </div>           
            }
            {
                props.header === undefined 
                ?   <></>
                :   <div>
                        <h3>{ props.header }</h3>
                        <br></br>
                    </div>           
            }
            {
                props.body === undefined 
                ? <></>
                :   <div>
                        <p>{ formatText(props.body) }</p>
                        <br></br>
                    </div>  
            }
            {
                props.img === undefined 
                ? <></>
                : 
                    <div>
                        <img 
                            src={ props.img } 
                            className={ props.imgType===undefined ? "subsection-img" : props.imgType }
                        >
                        </img>
                    </div>
            }
        </div>
    );
}
export default Section;
