import React from 'react';
import '../../App.css';
import './Pages.css';
import Section from '../ProjectSections.js'
import csvFile from '../data/projects].csv'

function Projects() {
  const csvArray = processCSV()

  return( 
    <div className="project-section">
      {csvArray.length>0?
        <>
        {
          csvArray.map((item, i) => {
            <Section
              key={ i }
              href={ "/" + i }
              title={ item.title }
              category={ item.category }
              dates={ item.dates }
              body={ item.summary }
              link={ item.link }
            />
          })
        }
      </> : null}
    </div>
  )
}

export default Projects;

async function processCSV() {
  const str = csvFile;
  const delim = ','

  const headers = str.slice(0,str.indexOf('\n')).split(delim);
  const rows = str.slice(str.indexOf('\n')+1).split('\n');

  const newArray = rows.map( row => {
    const values = row.split(delim);
    const eachObject = headers.reduce((obj, header, i) => {
        obj[header] = values[i];
        return obj;
    }, {})
    return eachObject;
  })

  return newArray;
}

