import React from 'react';

import '../../App.css';
import './Pages.css';

import Footer from '../Footer/Footer.js'

import hello from './aboutMedia/hello.png'
import profile from './aboutMedia/profile.png'
import acrylicPouring from './aboutMedia/acrylicPouring.jpeg'
import balloonBirthdayAnimation from './aboutMedia/balloonBirthdayAnimation.GIF'
import bearyCuteCake from './aboutMedia/bearyCuteCake.jpg'
import caliFireBox from './aboutMedia/caliFireBox.jpeg'
import catSprout from './aboutMedia/catSprout.jpeg'
import desktopOrganizerPrototype from './aboutMedia/desktopOrganizerPrototype.jpeg'
import fatherCoffeeCake from './aboutMedia/fatherCoffeeCake.jpeg'
import flowerCakeRoll from './aboutMedia/flowerCakeRoll.jpg'
import linocut from './aboutMedia/linocut.jpeg'
import littleFigures from './aboutMedia/littleFigures.jpeg'
import mendingPractice from './aboutMedia/mendingPractice.jpeg'
import monetEmbroidery from './aboutMedia/monetEmbroidery.jpeg'
import motorizedRollerskate from './aboutMedia/motorizedRollerskate.jpeg'
import plasticWaste from './aboutMedia/plasticWaste.jpeg'
import watercolorIsland from './aboutMedia/watercolorIsland.jpeg'
import weavingGarden from './aboutMedia/weavingGarden.jpeg'


function GalleryItem(src, text) {
    return  <div className="gallery-about-container">
                <img src={src} className="about-media" alt={text}></img>
                <p>{text}</p>
            </div>
}

function About() {
    return (
        <>
            <div className="about-container">
                <div className="left-about">
                    <img src={hello} className="about-media-small"></img>
                    <div className="divider"></div>
                    <p>
                        Erica Fu is a UI/UX designer, webapp developer, and student club leader at Carnegie Mellon University. 
                    </p>
                    <br></br>
                    <p>
                        Throughout her work and play, Erica loves to create. Because of this, she has made a variety of things through different mediums. She is currently interested in crocheting and flower pressing.
                    </p>
                </div>
                <div className="right-about">
                    <img src={profile} className="about-media"></img>
                </div>
            </div>
            <div className="gallery-about">
                {GalleryItem(acrylicPouring, "Experimented with acrylic pouring")}
                {GalleryItem(bearyCuteCake, "Baked a beary cute cake")}
                {GalleryItem(caliFireBox, "Created a fire box depicting California fires for a textiles course")}
                {GalleryItem(desktopOrganizerPrototype, "Prototyped a desktop organizer for a product design course")}
                {GalleryItem(balloonBirthdayAnimation, "Created a balloon animation as a birthday gift to a friend")}
                {GalleryItem(littleFigures, "Molded little clay figures with my mother")}
                {GalleryItem(catSprout, "Crocheted a sprout for my cat (she did not like it)")}
                {GalleryItem(flowerCakeRoll, "Baked an ube flower cake roll")}
                {GalleryItem(fatherCoffeeCake, "Baked a coffee cake for my father")}
                {GalleryItem(linocut, "Experiemented with linocut")}
                {GalleryItem(mendingPractice, "Loosely mended a piece of felt for a textiles course")}
                {GalleryItem(linocut, "Experiemented with linocut")}
                {GalleryItem(linocut, "Experiemented with linocut")}
                {GalleryItem(linocut, "Experiemented with linocut")}
                {GalleryItem(linocut, "Experiemented with linocut")}
                {GalleryItem(linocut, "Experiemented with linocut")}


                <img src={mendingPractice} className="about-media" alt="Loosely mended a piece of felt for a textiles course"></img>
                <img src={monetEmbroidery} className="about-media" alt="Embroidered a piece inspired by Monet's paintings"></img>
                <img src={motorizedRollerskate} className="about-media" alt="Constructed motorized rollerskates for an engineering hackathon"></img>
                <img src={watercolorIsland} className="about-media" alt="Painted Bagnoregio with watercolor"></img>
                <img src={plasticWaste} className="about-media" alt="Weaved plastic bags to show the unnecessary amount of waste my roommates and I received from campus"></img>
                <img src={weavingGarden} className="about-media" alt="Weaved a landscape for a textiles course"></img>
            </div>
            <Footer />
        </>
    );
}

export default About;
