import React from 'react';
import { NavLink } from 'react-router-dom';

import './Pages.css';
import Socials from '../components/Socials/Socials.js'
import UXProjects from '../components/UXProjects/UXProjects.js'
import gif from '../media/welcome.gif'
import DevPreview from '../media/DevPreview.png'
import About from '../components/About/About.js'
import { makeTags } from '../components/data/Data.js'

function Home() {

	const scrollToTop = () => {
		window.scrollTo({top: 0, behavior: 'smooth'});
	};

  const tags = "Website,React,HTML • CSS • JavaScript,Physical Computing,Arduino,Xcode • Swift,iOS App,Ruby on Rails,React Native,Mobile App"

  return (
      <>
        <div id="home-background">
          <div className="home-gif">
            <img src={gif} className="home-gif" alt="loading..." />
          </div>
          <div className="page-body">
            <div style={{padding: "125px"}}></div>
            {/* <div className="lab-links body">
              <h6>UX designer and researcher who makes complex concepts into simple experiences</h6>
              <br></br>
              <h5>Information Systems and Human Computer Interaction at <b>Carnegie Mellon</b></h5>
              <h5>Currently working at the&nbsp; 
                <a target='_blank' href='http://3.210.81.13/about_us'><b>CHIMPs</b></a>,&nbsp; 
                <a target='_blank' href='https://dig.cmu.edu/'><b>DIG</b></a>, and&nbsp; 
                <a target='_blank' href='http://coexlab.com/'><b>CoEx</b></a> labs
              </h5>
              <h5>Previously designing at&nbsp;
                <a target='_blank' href='https://www.equinix.com/'><b>Equinix</b></a>
              </h5>
            </div> */}
            <div className="lab-links body">
              <h6>UX designer and researcher who makes complex concepts into simple experiences</h6>
              <br></br><br></br><br></br>
              <ScrollInNav scrollInHeight={150}>
              <h5>Information Systems and Human Computer Interaction at <b>Carnegie Mellon</b>. Currently working at the&nbsp; 
                <a target='_blank' href='http://3.210.81.13/about_us'><b>CHIMPs</b></a>,&nbsp; 
                <a target='_blank' href='https://dig.cmu.edu/'><b>DIG</b></a>, and&nbsp; 
                <a target='_blank' href='http://coexlab.com/'><b>CoEx</b></a> labs.
              Previously designing at&nbsp;
                <a target='_blank' href='https://www.equinix.com/'><b>Equinix</b></a>.
              </h5>
        </ScrollInNav>
            </div>
          </div>
        </div>
        <div className="body">
          <UXProjects />
          <NavLink to='/Developer' className="no-underline" onClick={scrollToTop}>
            <div className='dev-link-container'>
              <div className='dev-link'>
                <h2>With my background in Information Systems, I have also worked on a variety of websites, mobile applications, and physical computing projects.</h2>
                <div className="featured-tags dev-tags">{ makeTags(tags) }</div>
              </div>
              <img src={DevPreview} className="dev-img" alt="Development project preview" />
            </div>
					</NavLink>
          <div className="divider-big"></div>
          <About />
        </div>
      </>
  );
}

export default Home;
