import React from "react";
import './FeaturedIntro.css'; 
import '../../App.css'

import ThirtyDayIntro from './media/ThirtyDayIntro.png'

// function to formas a string to include newlines
function formatText(str) {
    if (!str) return str;

    // replace \n in the string with <br />
    let formatted = [];
    const lines = str.split("\\n");

    for (let line of lines) {
        formatted.push(line);
        formatted.push(<br />);

    }
    return formatted;
}

function featuredImg(id) {
    switch (id) {
        case "thirty_day_adulting":
            return <h1>Thirty Day Adulting</h1>;
            // <img src={ ThirtyDayIntro } className="featured-img"></img>;   
    }
}

function FeaturedIntro(props) {

    const projectId = props.img.toString().trim();

    return(
        <>
            <div className="featured-intro-text">
                <h2>{ props.title }</h2>
                <div className="divider"></div>
                <h5>{ props.date }</h5>
                <div className="divider"></div>
                <p>{ formatText(props.summary) }</p>
                <div className="divider"></div>
                <h5>Responsibilities:</h5>
                <p>{ props.responsibilities }</p>
                <div className="divider"></div>
                <h5>Tools:</h5>
                <p>{ props.tools }</p>
            </div>
            {/* <h1>{ projectId }</h1>
            { featuredImg(projectId) } */}
            <img src={ featuredImg(props.id) } className="featured-img"></img>
        </>
    )
}

export default FeaturedIntro
