import React from "react";
import './Data.css'; 
import '../../App.css'

import ThirtyDay from './media/30Day.png'
import ReducingSingleUse from './media/SingleUse.png'
import sustain_cmu from './media/SustainCMU.png'
import trip_pic from './media/TRIPPIC.png'

// function to create purple tags
function makeTags(str) {
    if (!str) return str;

    const tags = str.split(",");
    const final = []

    for (let tag of tags) {
        final.push(
            <div className="tag">
                { tag }
            </div>
        ) 
    }

    return final;
}

// function to format a string to include newlines
function formatText(str) {
    if (!str) return str;

    // replace \n in the string with <br />
    let formatted = [];
    const lines = str.split("\n");

    for (let line of lines) {
        formatted.push(line);
        formatted.push(<br />);

    }

    return formatted;
}

// extract data from a tsv to a csv array
const UXProjectData = () => {

    const strRead = `Component	Title	Highlight	Tags	Dates	Summary	Link	LinkTitle	Responsibilities	Tools	Team~
    ThirtyDayAdulting	30-Day Adulting	iOS application to teach and encourage cybersecurity habits	Research,Figma,Xcode • Swift,Firebase,Mobile App	May 2021 - present	Developed an adulting challenge iOS application that uses embedded design and social influence principles to effectively teach and encourage cybersecurity habits.\nWorking as a research assistant for the CoEx lab at CMU.			App Development, UI/UX, Research	Xcode\nFirebase\nFigma	Isadora Krsek, Nellie Tonev, Sherry Chen~
    TripPic	TRIP-PIC	All-in-one tool to generate scheduled itineraries for travel	Research,Contextual Inquiries,Prototyping,Project Management	Jan 2022 - May 2022	Streamlining travel planning for young tech-savvy users by combining a visual-focused interface with social media to generate scheduled itineraries for trips.			Project Management, Contextual Inquiries, Prototyping	Miro\nFigma	Heysu Oh, Advait Wadhwani~
    ReducingSingleUse	Reducing single-use	Service design to reduce single-use at CMU dining	Service Design,Research,Contextual Inquiries, Figma	Aug 2021 - Dec 2021	Designed a solution to incorporate reusable containers to reduce the amoung of single use from dining services while still supporting college student dining habits on and off campus.			Contextual Inquiries, Service Design, Communication Design	Figma	Julianna Bolivar, Gia Marino, Karan Shah~
    SustainCMU	Sustain CMU	Communication design to correct waste disposal behavior at CMU	Human-Centered Design, Communication Design, Parallel Prototyping	Jan 2022 - May 2022	Using communication design to make students aware of the waste management issues at CMU and correcting waste disposal habits.	https://www.dfaxcmu.org/sustain-cmu	Visit Project	Prototyping, User Testing, Communication Design	Figma	Kelly Wang, Julianna Bolivar, Catherine Liu`

    return csvArray(strRead, '\t');
}

function projectProps(str) {
    const arr = UXProjectData();
    // console.log(arr);
    return arr.find((elem) => {return elem.Component === str});
}

function csvArray(str, delim) {
    const headers = str.slice(0,str.indexOf('~')).split(delim);
    const rows = str.slice(str.indexOf('~')+1).split('~');

    const newArray = rows.map( row => {
      const values = row.split(delim);
      const eachObject = headers.reduce((obj, header, i) => {
          obj[header] = values[i].trim();
          return obj;
      }, {})
      return eachObject;
    })

    return newArray;
}

function projectImg(id) {
    switch (id) {
        case "ThirtyDayAdulting":
            return  thirty_day_adulting;
        case "ReducingSingleUse":
            return  reducing_singleuse;
        case "TripPic":
            return  trip_pic;
        case "SustainCMU":
            return sustain_cmu;
        // case "twentyfour":
        //     return  twentyfour;
        // case "sentence_mosaics":
        //     return  sentence_mosaics;
        // case "dfa_website":
        //     return  dfa_website;
        // case "duquesne_incline":
        //     return  duquesne_incline;
        // case "microsite":
        //     return  microsite;
        // case "ccc":
        //     return  ccc;
        // case "acrylic_pour_painting_platform":
        //     return  acrylic_pour_painting_platform;
    }
}

export { makeTags, formatText, UXProjectData, projectProps, projectImg }
